import React, { useState } from 'react';
import { Tabs, Tab } from 'react-bootstrap';
import { useVisibility } from '../context/VisibilityContext';

const TabComponent = () => {
    const { isVisible } = useVisibility();
    const [activeKey, setActiveKey] = useState('filtrosVisuales');
    const [selectedParticionHA, setSelectedParticionHA] = useState([]);
    const [selectedPiso, setSelectedPiso] = useState([]);

    const onSelect = (k) => {
        setActiveKey(k);
    };

    const getTabIcon = (key) => {
        if (key === 'filtrosVisuales') {
            return activeKey === 'filtrosVisuales' ? 'images/eyered.svg' : 'images/eyewhite.svg';
        } else if (key === 'barrasPedidos') {
            return activeKey === 'barrasPedidos' ? 'images/barrasred.svg' : 'images/barraswhite.svg';
        }
    };

    const handleParticionHAChange = (event) => {
        setSelectedParticionHA(Array.from(event.target.selectedOptions, option => option.value));
    };

    const handlePisoChange = (event) => {
        setSelectedPiso(Array.from(event.target.selectedOptions, option => option.value));
    };

    const tabStyle = {
        position: 'fixed',
        top: '35%',
        right: '50px',
        transform: 'translateY(-50%)',
        zIndex: 1000,
        width: '450px',
        height: '385px',
        overflow: 'auto'
    };

    const tabContentStyle = {
        backgroundColor: 'white',
        borderRadius: '0 20px 20px 20px',
        padding: '15px',
        height: '100%',
        overflowY: 'auto'
    };

    const tabHeaderStyle = {
        borderRadius: '30px 30px 0 0',
    };

    return isVisible ? (
        <div style={tabStyle}>
            <Tabs defaultActiveKey="filtrosVisuales" id="tab-component" onSelect={onSelect} style={tabHeaderStyle}>
                <Tab eventKey="filtrosVisuales" title={<span><img src={getTabIcon('filtrosVisuales')} alt="Icono Filtros Visuales" /> Filtros visuales</span>}>
                    <div style={tabContentStyle}>
                        <div>
                            <label htmlFor="particionHA">Valores para AEC partición HA</label>
                            <select multiple value={selectedParticionHA} onChange={handleParticionHAChange} id="particionHA">
                                <option value="opcion1">Opción 1</option>
                                <option value="opcion2">Opción 2</option>
                                {/* Agrega más opciones según sea necesario */}
                            </select>
                        </div>
                        <div>
                            <label htmlFor="piso">Valores para AEC Piso</label>
                            <select multiple value={selectedPiso} onChange={handlePisoChange} id="piso">
                                <option value="opcion1">Opción 1</option>
                                <option value="opcion2">Opción 2</option>
                                {/* Agrega más opciones según sea necesario */}
                            </select>
                        </div>
                    </div>
                </Tab>
                {/* Aquí puedes agregar más pestañas si es necesario */}
            </Tabs>
        </div>
    ) : null;
};

export default TabComponent;
