import React, { useEffect } from 'react';
import Viewer from './Viewer';
import HeaderApp from './HeaderApp';
import ListadoProyectos from './proyectos/ListadoProyectos';

const Proyectos = ({ token, urn, selectedIds, onCameraChange, onSelectionChange }) => {
    const estiloProyectos = {
        backgroundColor: '#D8D8D8',
        padding: '20px',
        height: 'calc(100vh - 64px)',
        overflowY: 'scroll',
    };

    const estiloViewerContainer = {
        height: '400px',
        width: '20%'
    };

    useEffect(() => {
        const updateViewerStyle = () => {
            const viewerElement = document.querySelector('.adsk-viewing-viewer');
            if (viewerElement) {
                viewerElement.style.height = '400px !important';
                viewerElement.style.width = '20% !important';
                viewerElement.style.overflow = 'hidden';
            }
        };

        updateViewerStyle();

        // Opcional: Actualizar el estilo cuando la ventana cambia de tamaño
        window.addEventListener('resize', updateViewerStyle);
        return () => {
            window.removeEventListener('resize', updateViewerStyle);
        };
    }, []); // El arreglo vacío asegura que este efecto solo se ejecute una vez

    return (
        <div>
            <HeaderApp />
            <div style={estiloProyectos}>
                <div className='row'>
                    <div className='col-6' >
                        <ListadoProyectos /> {/* Instanciar ListadoProyectos */}
                        {/* Espacio para contenido adicional */}
                    </div>
                    <div className='col-6'>
                        <div className='row'>
                            <div className='col-6' style={estiloViewerContainer}>
                                <Viewer 
                                    runtime={{ accessToken: token }}
                                    urn={urn}
                                    selectedIds={selectedIds}
                                    onCameraChange={onCameraChange}
                                    onSelectionChange={onSelectionChange}
                                />
                            </div>
                            <div className='col-6'>
                                <p>Aquí se mostrarán los proyectos...</p>
                            </div>
                        </div>
                        <div className='row'>
                            <div className='col-12' style={estiloViewerContainer}>
                                
                            </div>
                            
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Proyectos;
