import React, { useState } from 'react';
import Select from 'react-select';

const AdministradorDeVistas = () => {
    const [vistaSeleccionada, setVistaSeleccionada] = useState(null);
    const opcionesDeVistas = [
        { value: 'vista1', label: 'Vista 1' },
        { value: 'vista2', label: 'Vista 2' },
        // Agrega más opciones según las vistas disponibles
    ];

    const estiloDelComponente = {
        width: '450px', // Mismo ancho que TabsComponent
        marginTop: '20px', // Espaciado respecto al componente superior
        position: 'relative', // Posicionamiento
        backgroundColor: '#FFF', // Color de fondo
        padding: '10px', // Relleno interno
        borderRadius: '10px', // Bordes redondeados
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)' // Sombra para el efecto flotante
    };

    const estiloCabecera = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '15px' // Espacio debajo de la cabecera
    };

    const estiloBoton = {
        display: 'flex',
        alignItems: 'center',
        border: 'none',
        backgroundColor: 'transparent',
        cursor: 'pointer'
    };

    return (
        <div style={estiloDelComponente}>
            <div style={estiloCabecera}>
                <span>Administrador de Vistas</span>
                <button style={estiloBoton}>
                    <img src="ruta-a-tu-icono.jpg" alt="Icono" style={{ marginRight: '5px' }} />
                    Texto del Botón
                </button>
            </div>
            <Select
                options={opcionesDeVistas}
                value={vistaSeleccionada}
                onChange={setVistaSeleccionada}
            />
            <div style={{ height: '35px' }}></div> {/* Espacio en la última fila */}
        </div>
    );
};

export default AdministradorDeVistas;
