// src/components/App.jsx
import React, { useState } from 'react';
import ColumnaIzquierda from './ColumnaIzquierda';
import ColumnaDerecha from './ColumnaDerecha';
import './App.css';

const App = ({ token, urn }) => {
    const [isCollapsed, setIsCollapsed] = useState(false);
    const [selectedIds, setSelectedIds] = useState([]);
    const [camera, setCamera] = useState(null);
    let wrapper = null;

    const handleInputChange = (ev) => {
        // ... lógica para manejar cambios de input ...
    };

    return (
        <div className="container-fluid">
            <div className="row">
                <ColumnaIzquierda isCollapsed={isCollapsed} handleCollapse={() => setIsCollapsed(!isCollapsed)} />
                <ColumnaDerecha
                    isCollapsed={isCollapsed}
                    token={token}
                    urn={urn}
                    selectedIds={selectedIds}
                    onCameraChange={({ viewer, camera }) => setCamera(camera.getWorldPosition())}
                    onSelectionChange={({ viewer, ids }) => setSelectedIds(ids)}
                    refViewer={ref => wrapper = ref}
                />
            </div>
        </div>
    );
};

export default App;
