import React, { useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';

const ListadoProyectos = () => {
  const [proyectoSeleccionado, setProyectoSeleccionado] = useState('');

  const handleSelectChange = (event) => {
    setProyectoSeleccionado(event.target.value);
  };

  const estiloListadoProyectos = {
    backgroundColor: '#D8D8D8', // Color de fondo
    padding: '20px', // Agrega un poco de espacio alrededor del contenido
    height: 'calc(100vh - 64px)', // Altura total de la ventana menos la altura del HeaderApp
    overflowY: 'scroll', // Activa el desplazamiento vertical
};

  // Genera las opciones para el select
  const opcionesProyectos = [];
  for (let i = 1; i <= 15; i++) {
    opcionesProyectos.push(<MenuItem value={`Proyecto ${i}`}>Proyecto {i}</MenuItem>);
  }

  return (
    <Card>
      <CardContent>
      <div style={estiloListadoProyectos}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src="ruta-al-icono.svg" alt="Icono" style={{ marginRight: '10px' }} />
          <Typography variant="h6" style={{ fontSize: 14, fontWeight: 'bold' }}>
            Proyectos
          </Typography>
        </div>
        <Typography variant="body2" style={{ marginTop: '10px' }}>
          Directorio de Proyectos Disponibles
        </Typography>
        <div style={{ marginTop: '10px' }}>
          <Button variant="contained" style={{ marginRight: '10px' }}>
            <img src="ruta-al-icono.svg" alt="Botón" style={{ marginRight: '5px' }} />
            Botón
          </Button>
          <Button variant="contained" style={{ marginRight: '10px' }}>
            <img src="ruta-al-icono.svg" alt="Botón" style={{ marginRight: '5px' }} />
            Botón
          </Button>
          <Button variant="contained">
            <img src="ruta-al-icono.svg" alt="Botón" style={{ marginRight: '5px' }} />
            Botón
          </Button>
        </div>
        <FormControl fullWidth style={{ marginTop: '20px' }}>
          <Select
            value={proyectoSeleccionado}
            onChange={handleSelectChange}
            displayEmpty
            inputProps={{ 'aria-label': 'Without label' }}
          >
            {opcionesProyectos}
          </Select>
        </FormControl>
        </div>
      </CardContent>
    </Card>
  );
};

export default ListadoProyectos;
