import React from 'react';
import { Tabs, Tab } from 'react-bootstrap';
import { useVisibility } from '../context/VisibilityContext';
const TabComponent = () => {
    const { isVisible } = useVisibility();

    const emitVisibilityChangeEvent = () => {
        const event = new CustomEvent('toggleTabVisibility');
        window.dispatchEvent(event);
    };

    
    const tabStyle = {
        position: 'fixed', // O 'absolute' si estás dentro de un contenedor relativo
        top: '35%',
        right: '50px',
        transform: 'translateY(-50%)',
        zIndex: 1000, // Asegura que esté sobre otros elementos
        width: '450px', // Ancho fijo
        height: '385px', // Altura fija
        overflow: 'auto' // Añadir scroll si el contenido es muy grande
        
 // otros estilos que necesites
    };
    const tabContentStyle = {
        backgroundColor: 'white',
        borderRadius: '0 0 10px 10px', // Redondea solo las esquinas inferiores
        padding: '15px',
        height: '100%',
        overflowY: 'auto'
    };
    const nomarginbottom ={
        marginBottom: '0px !important'
    }
    const customTabStyle = isActive => ({
        backgroundColor: isActive ? '#FFF !important' : '#14130F !important',
        color: isActive ? '#000 !important' : '#FFF !important'
    });
    return (
        isVisible ? (
        <div style={tabStyle}>
            <Tabs defaultActiveKey="filtrosVisuales" id="tab-component" className="nomarginbottom"  style={{ marginBottom: '0 !important' }}>
            <Tab eventKey="filtrosVisuales" title={<span><img src={'images/eyereds.svg'} alt="Icono 1" /> Filtros visuales</span>} className="tab-inactive">
                    {/* Contenido de Tab 1 */}
                    <div style={tabContentStyle}>
                    <label htmlFor="input1">Label 1</label>
                    <input type="text" id="input1" />
                    <label htmlFor="input2">Label 2</label>
                    <input type="text" id="input2" />
                </div>
                </Tab>
                <Tab eventKey="barrasPedidos" title={<span><img src={'images/estadisticas.svg'} alt="Icono 2" /> Barras & Pedidos</span>} className="tab-active">
                    {/* Contenido de Tab 2 */}
                    <div style={tabContentStyle}>
                    <table>
                    <thead>
                        <tr>
                            <th>Columna 1</th>
                            <th>Columna 2</th>
                            <th>Columna 3</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>Dato 1</td>
                            <td>Dato 2</td>
                            <td>Dato 3</td>
                        </tr>
                        {/* Agrega más filas según sea necesario */}
                    </tbody>
                </table>
                </div>
                </Tab>
            </Tabs>
        </div>):null
    );
};

export default TabComponent;
