import React from 'react';
import Viewer from './Viewer'; // Asegúrate de que la ruta sea correcta
import Footer from './Footer'; // Asegúrate de que la ruta sea correcta

const ColumnaDerecha = ({ isCollapsed, token, urn, selectedIds, onCameraChange, onSelectionChange, refViewer }) => {
    return (
        <div className={isCollapsed ? 'col-11' : 'col-9'}>
            {/* Visualizador en su propia fila */}
            <div className="row" style={{ height: '100%' }}> {/* Altura ajustada para dejar espacio para el footer */}
                <div className="col">
                    <Viewer
                        runtime={{ accessToken: token }}
                        urn={urn}
                        selectedIds={selectedIds}
                        onCameraChange={onCameraChange}
                        onSelectionChange={onSelectionChange}
                        ref={refViewer}
                    />
                </div>
            </div>

            {/* Footer en su propia fila */}
            <div className="row" style={{ height: '100px' }}> {/* Altura fija para el footer */}
                <div className="col">
                    <Footer />
                </div>
            </div>
        </div>
    );
};

export default ColumnaDerecha;
